using System; using System.Collections.Generic; using System.ComponentModel; using System.Data; using System.Drawing; using System.Linq; using System.Text; using System.Windows.Forms; namespace Loop_Program { public partial class Form1 : Form { public Form1() { InitializeComponent(); } private void btn3_Click(object sender, EventArgs e) { do { int int1 = 0; int1++; } while (Microsoft.VisualBasic.Interaction.InputBox("type -1 to end the loop", "Loop") != "-1"); } private void btn1_Click(object sender, EventArgs e) { for (int intLoop = 0; intLoop < 10; intLoop += 0) { intLoop += 2; lbl1.Text = intLoop.ToString(); } } private void btn2_Click(object sender, EventArgs e) { for (int intLoop2 = 10; intLoop2 > 1; intLoop2 -= 1) lbl2.Text = intLoop2.ToString(); } private void btn4_Click(object sender, EventArgs e) { do { } while (Microsoft.VisualBasic.Interaction.InputBox("type stop to stop the loop", "loop") != "stop");  } private void btn5_Click(object sender, EventArgs e) { int intNum = 1; while (intNum < 10) { intNum++; lbl3.Text = intNum.ToString(); } } private void btn6_Click(object sender, EventArgs e) { int intNum = 1; do { intNum += 1; label4.Text = intNum.ToString(); } while (intNum < 10);  } } }